mapper.select:
select
id, userId, goodsId, status, amount, createdTime, updatedTime
from qg_goods_message


        and id = #{id}


        and userId = #{userId}


        and goodsId = #{goodsId}


        and status = #{status}


        and amount = #{amount}


        and createdTime = #{createdTime}


        and updatedTime = #{updatedTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from qg_goods_message


        and id = #{id}


        and userId = #{userId}


        and goodsId = #{goodsId}


        and status = #{status}


        and amount = #{amount}


        and createdTime = #{createdTime}


        and updatedTime = #{updatedTime}
